// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;

contract DocumentCertification {
    address public owner;

    struct Document {
        string contentHash;  
        uint256 timestamp;    
        string owner;
        string fileUrl;
    }

    mapping(string => Document) public certifiedDocuments;

    modifier onlyOwner() {
        require(msg.sender == owner, "Only the contract owner can perform this action");
        _;
    }

    constructor() public {
        owner = msg.sender;
    }

    function certifyDocument(string memory _contentHash) public {
        require(bytes(_contentHash).length > 0, "Content hash cannot be empty");
        require(certifiedDocuments[_contentHash].timestamp == 0, "Document is already certified");

        Document memory newDocument = Document({
            contentHash: _contentHash,
            timestamp: block.timestamp,
            owner: "mbula Mboma",
            fileUrl: "http://google.com"
        });

        certifiedDocuments[_contentHash] = newDocument;
    }

}
